const ComponentExample stepperExample1 = ComponentExample(
  title: "Vertical Example",
  builder: _buildStepperExample1,
  code: "import 'package:docs/pages/docs/components/carousel_example.dart';\nimport 'package:docs/shadcn_ui.dart';\n\nclass StepperExample1 extends StatefulWidget {\n  const StepperExample1({super.key});\n\n  @override\n  State<StepperExample1> createState() => _StepperExample1State();\n}\n\nclass _StepperExample1State extends State<StepperExample1> {\n  final StepperController controller = StepperController();\n\n  @override\n  Widget build(BuildContext context) {\n    return Stepper(\n      controller: controller,\n      // Vertical layout with 3 steps and Next/Prev actions.\n      direction: Axis.vertical,\n      steps: [\n        Step(\n          title: const Text('Step 1'),\n          contentBuilder: (context) {\n            return StepContainer(\n              actions: [\n                const SecondaryButton(\n                  child: Text('Prev'),\n                ),\n                PrimaryButton(\n                    child: const Text('Next'),\n                    onPressed: () {\n                      // Advance to the next step.\n                      controller.nextStep();\n                    }),\n              ],\n              child: const NumberedContainer(\n                index: 1,\n                height: 200,\n              ),\n            );\n          },\n        ),\n        Step(\n          title: const Text('Step 2'),\n          contentBuilder: (context) {\n            return StepContainer(\n              actions: [\n                SecondaryButton(\n                  child: const Text('Prev'),\n                  onPressed: () {\n                    // Move back one step.\n                    controller.previousStep();\n                  },\n                ),\n                PrimaryButton(\n                    child: const Text('Next'),\n                    onPressed: () {\n                      controller.nextStep();\n                    }),\n              ],\n              child: const NumberedContainer(\n                index: 2,\n                height: 200,\n              ),\n            );\n          },\n        ),\n        Step(\n          title: const Text('Step 3'),\n          contentBuilder: (context) {\n            return StepContainer(\n              actions: [\n                SecondaryButton(\n                  child: const Text('Prev'),\n                  onPressed: () {\n                    controller.previousStep();\n                  },\n                ),\n                PrimaryButton(\n                    child: const Text('Finish'),\n                    onPressed: () {\n                      controller.nextStep();\n                    }),\n              ],\n              child: const NumberedContainer(\n                index: 3,\n                height: 200,\n              ),\n            );\n          },\n        ),\n      ],\n    );\n  }\n}\n",
);

Widget _buildStepperExample1(BuildContext context) {
  return const StepperExample1();
}

