  builder: _buildStepperExample4,
  code: "import 'package:docs/pages/docs/components/carousel_example.dart';\nimport 'package:docs/shadcn_ui.dart';\n\nclass StepperExample4 extends StatefulWidget {\n  const StepperExample4({super.key});\n\n  @override\n  State<StepperExample4> createState() => _StepperExample4State();\n}\n\nclass _StepperExample4State extends State<StepperExample4> {\n  final StepperController controller = StepperController();\n\n  @override\n  Widget build(BuildContext context) {\n    return Stepper(\n      controller: controller,\n      direction: Axis.vertical,\n      steps: [\n        Step(\n          title: const Text('Step 1'),\n          icon: StepNumber(\n            onPressed: () {\n              // Tap on the step icon to jump directly to the step.\n              controller.jumpToStep(0);\n            },\n          ),\n          contentBuilder: (context) {\n            return StepContainer(\n              actions: [\n                const SecondaryButton(\n                  child: Text('Prev'),\n                ),\n                PrimaryButton(\n                    child: const Text('Next'),\n                    onPressed: () {\n                      controller.nextStep();\n                    }),\n              ],\n              child: const NumberedContainer(\n                index: 1,\n                height: 200,\n              ),\n            );\n          },\n        ),\n        Step(\n          title: const Text('Step 2'),\n          icon: StepNumber(\n            onPressed: () {\n              controller.jumpToStep(1);\n            },\n          ),\n          contentBuilder: (context) {\n            return StepContainer(\n              actions: [\n                SecondaryButton(\n                  child: const Text('Prev'),\n                  onPressed: () {\n                    controller.previousStep();\n                  },\n                ),\n                PrimaryButton(\n                    child: const Text('Next'),\n                    onPressed: () {\n                      controller.nextStep();\n                    }),\n              ],\n              child: const NumberedContainer(\n                index: 2,\n                height: 200,\n              ),\n            );\n          },\n        ),\n        Step(\n          title: const Text('Step 3'),\n          icon: StepNumber(\n            onPressed: () {\n              controller.jumpToStep(2);\n            },\n          ),\n          contentBuilder: (context) {\n            return StepContainer(\n              actions: [\n                SecondaryButton(\n                  child: const Text('Prev'),\n                  onPressed: () {\n                    controller.previousStep();\n                  },\n                ),\n                PrimaryButton(\n                    child: const Text('Finish'),\n                    onPressed: () {\n                      controller.nextStep();\n                    }),\n              ],\n              child: const NumberedContainer(\n                index: 3,\n                height: 200,\n              ),\n            );\n          },\n        ),\n      ],\n    );\n  }\n}\n",
);

