import 'package:flutter/widgets.dart';
import '../../component_example_models.dart';
import 'swiper_example_1.dart';

const ComponentExample swiperExample1 = ComponentExample(
  title: "Example 1",
  builder: _buildSwiperExample1,
  code: "import 'package:docs/shadcn_ui.dart';\n\nclass SwiperExample1 extends StatefulWidget {\n  const SwiperExample1({super.key});\n\n  @override\n  State<SwiperExample1> createState() => _SwiperExample1State();\n}\n\nclass _SwiperExample1State extends State<SwiperExample1> {\n  OverlayPosition _position = OverlayPosition.end;\n  bool _typeDrawer = true;\n\n  Widget _buildSelectPosition(OverlayPosition position, String label) {\n    return SelectedButton(\n      value: _position == position,\n      onChanged: (value) {\n        if (value) {\n          setState(() {\n            _position = position;\n          });\n        }\n      },\n      style: const ButtonStyle.outline(),\n      selectedStyle: const ButtonStyle.primary(),\n      child: Text(label),\n    );\n  }\n\n  @override\n  Widget build(BuildContext context) {\n    return Swiper(\n      builder: (context) {\n        return Container(\n          constraints: const BoxConstraints(\n            minWidth: 320,\n            minHeight: 320,\n          ),\n          child: Column(\n            mainAxisSize: MainAxisSize.min,\n            mainAxisAlignment: MainAxisAlignment.center,\n            children: [\n              const Text('Hello!'),\n              const Gap(24),\n              PrimaryButton(\n                onPressed: () {\n                  openDrawer(\n                      context: context,\n                      builder: (context) {\n                        return ListView.separated(\n                          itemCount: 1000,\n                          itemBuilder: (context, index) {\n                            return Card(\n                              child: Text('Item \$index'),\n                            );\n                          },\n                          separatorBuilder: (context, index) {\n                            return const Gap(8);\n                          },\n                        );\n                      },\n                      position: OverlayPosition.bottom);\n                },\n                child: const Text('Close'),\n              ),\n            ],\n          ),\n        );\n      },\n      // Swiper displays an overlay (drawer/sheet) that can be swiped in from a chosen edge.\n      position: _position,\n      // Choose the overlay type: Drawer slides over content; Sheet peeks up from an edge.\n      handler: _typeDrawer ? SwiperHandler.drawer : SwiperHandler.sheet,\n      child: SizedBox(\n        height: 500,\n        child: Card(\n          child: Center(\n            child: Column(\n              mainAxisSize: MainAxisSize.min,\n              children: [\n                const Text('Swipe me!'),\n                const Gap(24),\n                ButtonGroup(children: [\n                  _buildSelectPosition(OverlayPosition.left, 'Left'),\n                  _buildSelectPosition(OverlayPosition.right, 'Right'),\n                  _buildSelectPosition(OverlayPosition.top, 'Top'),\n                  _buildSelectPosition(OverlayPosition.bottom, 'Bottom'),\n                ]),\n                const Gap(24),\n                ButtonGroup(children: [\n                  Toggle(\n                    value: _typeDrawer,\n                    onChanged: (value) {\n                      setState(() {\n                        _typeDrawer = value;\n                      });\n                    },\n                    child: const Text('Drawer'),\n                  ),\n                  Toggle(\n                    value: !_typeDrawer,\n                    onChanged: (value) {\n                      setState(() {\n                        _typeDrawer = !value;\n                      });\n                    },\n                    child: const Text('Sheet'),\n                  ),\n                ]),\n              ],\n            ),\n          ),\n        ),\n      ),\n    );\n  }\n}\n",
);

Widget _buildSwiperExample1(BuildContext context) {
  return const SwiperExample1();
}

