import 'package:flutter/widgets.dart';
import '../../component_example_models.dart';
import 'toast_example_1.dart';

const ComponentExample toastExample1 = ComponentExample(
  title: "Example",
  builder: _buildToastExample1,
  code: "import 'package:docs/shadcn_ui.dart';\n\n// Demonstrates toast overlays in different screen locations with a custom\n// content builder and programmatic close via the overlay handle.\n\nclass ToastExample1 extends StatefulWidget {\n  const ToastExample1({super.key});\n\n  @override\n  State<ToastExample1> createState() => _ToastExample1State();\n}\n\nclass _ToastExample1State extends State<ToastExample1> {\n  // Builder for the toast content; receives an overlay handle so we can close it.\n  Widget buildToast(BuildContext context, ToastOverlay overlay) {\n    return SurfaceCard(\n      child: Basic(\n        title: const Text('Event has been created'),\n        subtitle: const Text('Sunday, July 07, 2024 at 12:00 PM'),\n        trailing: PrimaryButton(\n            size: ButtonSize.small,\n            onPressed: () {\n              // Close the toast programmatically when clicking Undo.\n              overlay.close();\n            },\n            child: const Text('Undo')),\n        trailingAlignment: Alignment.center,\n      ),\n    );\n  }\n\n  @override\n  Widget build(BuildContext context) {\n    return Wrap(\n      spacing: 8,\n      runSpacing: 8,\n      children: [\n        PrimaryButton(\n          onPressed: () {\n            showToast(\n              context: context,\n              builder: buildToast,\n              // Position bottom-left.\n              location: ToastLocation.bottomLeft,\n            );\n          },\n          child: const Text('Show Bottom Left Toast'),\n        ),\n        PrimaryButton(\n          onPressed: () {\n            showToast(\n              context: context,\n              builder: buildToast,\n              // Position bottom-right.\n              location: ToastLocation.bottomRight,\n            );\n          },\n          child: const Text('Show Bottom Right Toast'),\n        ),\n        PrimaryButton(\n          onPressed: () {\n            showToast(\n              context: context,\n              builder: buildToast,\n              // Position top-left.\n              location: ToastLocation.topLeft,\n            );\n          },\n          child: const Text('Show Top Left Toast'),\n        ),\n        PrimaryButton(\n          onPressed: () {\n            showToast(\n              context: context,\n              builder: buildToast,\n              // Position top-right.\n              location: ToastLocation.topRight,\n            );\n          },\n          child: const Text('Show Top Right Toast'),\n        ),\n        // bottom center\n        PrimaryButton(\n          onPressed: () {\n            showToast(\n              context: context,\n              builder: buildToast,\n              location: ToastLocation.bottomCenter,\n            );\n          },\n          child: const Text('Show Bottom Center Toast'),\n        ),\n        // top center\n        PrimaryButton(\n          onPressed: () {\n            showToast(\n              context: context,\n              builder: buildToast,\n              location: ToastLocation.topCenter,\n            );\n          },\n          child: const Text('Show Top Center Toast'),\n        ),\n      ],\n    );\n  }\n}\n",
);

Widget _buildToastExample1(BuildContext context) {
  return const ToastExample1();
}

const List<ComponentExample> toastExamples = [
  toastExample1,
];
