examples.minimal:
Validated<String>(
  validator: (value) => value == null || value.isEmpty
      ? ValidationResult.error('Required')
      : null,
  builder: (context, error, child) {
    return Column(
      children: [
        child!,
        if (error != null) Text(error.message),
      ],
    );
  },
  child: const TextField(),
)
